	}
	catch(ParserConfigurationException pce) {
		//pce.printStackTrace();
		log.error("Parser configureation exception occurred");
	}
	catch(SAXException se) {
		//se.printStackTrace();
		log.error("SAX exception occurred");
	}
	catch(IOException ioe) {
		//ioe.printStackTrace();
		log.error("IO exception occurred");
	}

	return;

