				//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
				driver.findElement(By.linkText("Usage Points")).click();	        

				//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
				driver.logStep("Then I should see a Usage Point");
				//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
				driver.assertContains(usagePointDescription1);

				} catch(Exception e) {
				testRunner.fail(driver.getErrorString())
				}

				//Close the browser
				driver.quit();

