				//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
				driver.findElement(By.linkText("Data Custodians")).click();

				//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
				driver.logStep("Then I should see a list of Data Custodians");
				//////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
				driver.assertContains("data_custodian");

				} catch(Exception e) {
				testRunner.fail(driver.getErrorString())
				}

				//Close the browser
				driver.quit();

