import java.net.*;
import java.io.*;

int issueGET(String targetURL,String strToken){

	log.info("Issuing malformed GET " + targetURL);
	int iRespCode = 0;
	String urlParameters = "";

	URL url;

	HttpURLConnection connection = null;  

	try {
		//Create connection
		url = new URL(targetURL);
		connection = (HttpURLConnection)url.openConnection();
		connection.setRequestMethod("GET");
		connection.setInstanceFollowRedirects(false);
		connection.setRequestProperty("Authorization", "Basic " + strToken);
		connection.setRequestProperty("Accept", "application/json, application/*+json");

		//Get Response    
		InputStream is = connection.getInputStream();
		BufferedReader rd = new BufferedReader(new InputStreamReader(is));
		String line;
		StringBuffer response = new StringBuffer(); 
		while((line = rd.readLine()) != null) {
			response.append(line);
			response.append('\r');
		}
		rd.close();
		//log.info response.toString();

		log.info "Response = " + connection.getResponseCode();

		iRespCode = connection.getResponseCode();

	} catch (Exception e) {
		log.info e.getMessage();
		return 0;
	} finally {
		if(connection != null) {
			log.info "Response = " + connection.getResponseCode();
			iRespCode = connection.getResponseCode();		
			connection.disconnect(); 
		}

		return iRespCode;
	}	
}

String strRedirURI = testRunner.testCase.getPropertyValue("redirect_uri");
String strAuthorizationServerTokenEndpoint = testRunner.testCase.getPropertyValue("authorizationServerTokenEndpoint");


int iTestID= 0;
while(true) {
	// loop through different malformation tests
	String strTestName = "";
	String strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";
	String strToken = "dGhpcmRfcGFydHk6c2VjcmV0";

	int iRespCode = 0;
	int iExpectedRespCode = 0;

	switch(iTestID) {
		case 0:
			strTestName = "Malformed Client Credentials Grant Access Token Request (No grant_type= field-value pair)	OAD041";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		case 1:
			strTestName = "Malformed Client Credentials Grant Access Token Request (No scope= field-value pair)	OAD042";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		case 2:
			strTestName = "Malformed Client Credentials Grant Access Token Request (Multiple grant_type= field-value pairs)	OAD043";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		case 3:
			strTestName = "Malformed Client Credentials Grant Access Token Request (Multiple scope= field-value pairs)	OAD044";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		case 4:
			strTestName = "Malformed Client Credentials Grant Access Token Request (contents of scope= field-value pair does not match contents of Application Information structure "scope" field)	OAD045";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		case 5:
			strTestName = "Invalid Client Credentials Grant Access Token Request (Application Information record grant_type field contains authorization_code, refresh_token)	OAD046";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		case 6:
			strTestName = "Valid JSON structure returned during Data Custodian Client Credentials Grant Access Token Request processing	OAD047";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;			
		default:
			break;
	}

	// 0 indicates skip test
	if(iExpectedRespCode != 0) {
		log.info("testing: " + strTestName);
		iRespCode = iRespCode = issueGET(strAuthorizationServerTokenEndpoint + strQryParams,strToken);

		if(iRespCode != iExpectedRespCode) {
			log.error("Failed malformed client credential grant access token request: " + strTestName + " expected response code:" + iExpectedRespCode + " but received:" + iRespCode);
			testRunner.fail("Failed malformed client credential grant access token request: " + strTestName + " expected response code:" + iExpectedRespCode + " but received:" + iRespCode);
			return;						
		}		
	} else {
		log.info("skipping: " + strTestName);
	}	

	iTestID++;
	if(iTestID > 6){
		break;
	}
}	



