	uri.path = url
	uri.query = params
	headers['Cookie'] = cookies.join(';')
	}
	}

	private HTTPBuilder initializeHttpBuilder() {
	def httpBuilder = new HTTPBuilder(baseUrl)

	httpBuilder.handler.success = { HttpResponseDecorator resp, reader ->
