import java.net.HttpUrlConnection.*;

public class codeLocation {

	private int iRespCode;
	private String strRespLoc;

	public codeLocation (int c, String l) {
		iRespCode = c;
		strRespLoc = l;
	}


	/******************************
	 * Return Response Code
	 ******************************/
	 public int getCode() {
	 	return iRespCode;
	 }


	/******************************
	 * Return Response Location
	 ******************************/
	 public String getLocation() {
	 	return strRespLoc;
	 }
}

int issueGET(String targetURL,String strSessionID){

	log.info("Issuing malformed GET " + targetURL);
	int iRespCode = 0;
	String urlParameters = "";

	URL url;

	HttpURLConnection connection = null;  

	try {
		//Create connection
		url = new URL(targetURL);
		connection = (HttpURLConnection)url.openConnection();
		connection.setRequestMethod("GET");
		connection.setInstanceFollowRedirects(false);
		connection.setRequestProperty("Cookie", "JSESSIONID=" + strSessionID);

		//Get Response    
		InputStream is = connection.getInputStream();
		BufferedReader rd = new BufferedReader(new InputStreamReader(is));
		String line;
		StringBuffer response = new StringBuffer(); 
		while((line = rd.readLine()) != null) {
			response.append(line);
			response.append('\r');
		}
		rd.close();
		log.info "response body = " + response.toString();

		log.info "Response code = " + connection.getResponseCode();
		log.info "Response message = " + connection.getHeaderField("Location");


		strRespLoc = connection.getHeaderField("Location");
		log.info "issueGET -- strRespLocation: " + strRespLoc;

		iRespCode = connection.getResponseCode();

	} catch (Exception e) {
		log.info e.getMessage();
		return 0;
	} finally {
		if(connection != null) {
			log.info "Response = " + connection.getResponseCode();
			iRespCode = connection.getResponseCode();		
			connection.disconnect(); 
		}

		return iRespCode;
	}	
}

