uri = project.getPropertyValue("thirdPartyNotificationEndpoint");
log.info "TP URI: " + uri;
uritemp = uri;
i = uritemp.indexOf("espi/1_1");
if(i>0) {
	// lets shorten it by stripping off up to resource
	uritemp = uritemp.substring(0,i-1);
