	// cant be a resource
	log.info ("Uri not well formed: " + uri);					
}



uritemp = project.getPropertyValue("resourceServerUri");
i = uritemp.indexOf("/",10);
j = uritemp.indexOf("//");
if(i>0) {
	// lets shorten it by stripping off up to resource
	uritemp = uritemp.substring(j+2,i);
	project.setPropertyValue("resourceServer",uritemp);
	log.info ("resourceServer: " + uritemp);					
