import java.net.*;
import java.io.*;

int issueGET(String targetURL,String strToken){

	log.info("Issuing malformed GET " + targetURL);
	int iRespCode = 0;
	String urlParameters = "";

	URL url;

	HttpURLConnection connection = null;  

	try {
		//Create connection
		url = new URL(targetURL);
		connection = (HttpURLConnection)url.openConnection();
		connection.setRequestMethod("GET");
		connection.setInstanceFollowRedirects(false);
		connection.setRequestProperty("Authorization", "Basic " + strToken);
		connection.setRequestProperty("Accept", "application/json, application/*+json");

		//Get Response    
		InputStream is = connection.getInputStream();
		BufferedReader rd = new BufferedReader(new InputStreamReader(is));
		String line;
		StringBuffer response = new StringBuffer(); 
		while((line = rd.readLine()) != null) {
			response.append(line);
			response.append('\r');
		}
		rd.close();

		log.info "Response Code = " + connection.getResponseCode();

		iRespCode = connection.getResponseCode();

	} catch (Exception e) {
		log.info e.getMessage();
		return 0;
	} finally {
		if(connection != null) {
			log.info "Response = " + connection.getResponseCode();
			iRespCode = connection.getResponseCode();		
			connection.disconnect(); 
		}

		return iRespCode;
	}	
}

String strOAuthCodeReceivedValue = context.mockService.getPropertyValue("OAuthCodeReceivedValue");
testRunner.testCase.setPropertyValue("OAuthCodeReceivedValue",strOAuthCodeReceivedValue);
String strRedirURI = testRunner.testCase.getPropertyValue("redirect_uri");
String strAuthorizationServerTokenEndpoint = testRunner.testCase.getPropertyValue("authorizationServerTokenEndpoint");


int iTestID= 0;
while(true) {
	// loop through different malformation tests
	String strTestName = "";
	String strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";
	String strToken = "dGhpcmRfcGFydHk6c2VjcmV0";

	int iRespCode = 0;
	int iExpectedRespCode = 0;

	switch(iTestID) {
		case 0:
			strTestName = "Access Token Request Authorization Header Rejection	OAD024";
			strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";
			strToken = "bad value";				
			iExpectedRespCode = 401;
			break;
		case 1:
			strTestName = "Malformed Authorization Code Grant Access Token Request (No grant_type field-value pair)	OAD025";
			strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";				
			iExpectedRespCode = 0;
			break;	
		case 2:
			strTestName = "Malformed Authorization Code Grant Access Token Request (No code field-value pair)	OAD026";
			strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";				
			iExpectedRespCode = 0;
			break;
		case 3:
			strTestName = "Malformed Authorization Code Grant Access Token Request (No redirect_uri field-value pair)	OAD027";
			strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";				
			iExpectedRespCode = 0;
			break;
		case 4:
			strTestName = "Malformed Authorization Code Grant Access Token Request (Multiple grant_type field-value pairs)	OAD028";
			strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";				
			iExpectedRespCode = 0;
			break;
		case 5:
			strTestName = "Malformed Authorization Code Grant Access Token Request (Multiple code field-value pairs)	OAD029";
			strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";				
			iExpectedRespCode = 0;
			break;
		case 6:
			strTestName = "Malformed Authorization Code Grant Access Token Request (Multiple redirect_uri field-value pairs)	OAD030";
			strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";				
			iExpectedRespCode = 0;
			break;
		case 7:
			strTestName = "Malformed Authorization Code Grant Access Token Request (Contains  HTTP BASIC Authorization header and client_id field-value pair)	OAD031";
			strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";				
			iExpectedRespCode = 0;
			break;
		case 8:
			strTestName = "Invalid Authorization Code Grant Access Token Request (redirect_uri field-value pair doess not match the redirect_uri field-value pair in the Authorization Code Request)	OAD032";
			strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";				
			iExpectedRespCode = 0;
			break;
		case 9:
			strTestName = "Invalid Authorization Code Grant Access Token Request (code field-value pair contains an authorization code issued to another client)	OAD033";
			strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";				
			iExpectedRespCode = 0;
			break;
		case 10:
			strTestName = "Invalid Authorization Code Grant Access Token Request (code field-value pair contains an invalid authorization code)	OAD034";
			strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";				
			iExpectedRespCode = 0;
			break;
		case 11:
		// TBD DIALOG BOX????
		//	Thread.sleep(60000);
			strTestName = "Invalid Authorization Code Grant Access Token Request (code field-value pair contains a valid authorization code issued 6 minutes prior)	OAD037";
			strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";				
			iExpectedRespCode = 0;
			break;
		case 12:
			strTestName = "Invalid Authorization Code Grant Access Token Request (Application Information record grant_type field-value pair contains client_credentials)	OAD040";
			strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";				
			iExpectedRespCode = 0;
			break;	
		default:
			break;
	}

	// 0 indicates skip test
	if(iExpectedRespCode != 0) {
		log.info("testing: " + strTestName);
		iRespCode = iRespCode = issueGET(strAuthorizationServerTokenEndpoint + strQryParams,strToken);

		if(iRespCode != iExpectedRespCode) {
			log.error("Failed malformed token request: " + strTestName + " expected response code:" + iExpectedRespCode + " but received:" + iRespCode);
			testRunner.fail("Failed malformed token request: " + strTestName + " expected response code:" + iExpectedRespCode + " but received:" + iRespCode);
			return;						
		}		
	} else {
		log.info("skipping: " + strTestName);
	}	

	iTestID++;
	if(iTestID > 12){
		break;
	}
}	

assert true;

