def authCase = testRunner.testCase.testSuite.project.testSuites['Library'].testCases['Create Authorization and extract all information'];

String strRedirURI = authCase.getPropertyValue("redirect_uri");
String strAuthorizationServerTokenEndpoint = authCase.getPropertyValue("authorizationServerTokenEndpoint");
String strOAuthCodeReceivedValue = authCase.getPropertyValue("OAuthCodeReceivedValue");
String strSelectedScope = authCase.getPropertyValue("scope");

int iTestID= 0;
while(true) {
	// loop through different malformation tests
	String strTestName = "";
	String strQryParams = "?redirect_uri=" + strRedirURI + "&code=" + strOAuthCodeReceivedValue + "&grant_type=authorization_code";
	String strToken = "dGhpcmRfcGFydHk6c2VjcmV0";

	int iRespCode = 0;
	int iExpectedRespCode = 0;

	switch(iTestID) {
		case 0:
			strTestName = "Malformed Client Credentials Grant Access Token Request (No grant_type= field-value pair)	OAD041";
			strQryParams = "?scope=" + strSelectedScope;			
			iExpectedRespCode = 400;
			break;
		case 1:
			strTestName = "Malformed Client Credentials Grant Access Token Request (No scope= field-value pair)	OAD042";
			strQryParams = "?grant_type=client_credentials";			
			iExpectedRespCode = 400;
			break;
		case 2:
			strTestName = "Malformed Client Credentials Grant Access Token Request (Multiple grant_type= field-value pairs)	OAD043";
			strQryParams = "?grant_type=client_credentials&&scope=" + strSelectedScope + "&grant_type=client_credentials";			
			iExpectedRespCode = 400;
			break;
		case 3:
			strTestName = "Malformed Client Credentials Grant Access Token Request (Multiple scope= field-value pairs)	OAD044";
			strQryParams = "?grant_type=client_credentials&&scope=" + strSelectedScope + "&&scope=" + strSelectedScope;			
			iExpectedRespCode = 400;
			break;
		case 4:
			strTestName = "Malformed Client Credentials Grant Access Token Request (contents of scope= field-value pair does not match contents of Application Information structure "scope" field)	OAD045";
			strQryParams = "?grant_type=client_credentials&&scope=FB=99";			
			iExpectedRespCode = 400;
			break;
		case 5:
			// This test case only requires the Application Information be verified 
			strTestName = "Invalid Client Credentials Grant Access Token Request (Application Information record grant_type field contains authorization_code, refresh_token)	OAD046";
			strQryParams = "?grant_type=client_credentials&&scope=" + strSelectedScope;			
			iExpectedRespCode = 0;
			break;	
		default:
			break;
	}

	// 0 indicates skip test
	if(iExpectedRespCode != 0) {
		log.info("testing: " + strTestName);
		iRespCode = iRespCode = issueGET(strAuthorizationServerTokenEndpoint + strQryParams,strToken);

		if(iRespCode != iExpectedRespCode) {
			log.error("Failed malformed client credential grant access token request: " + strTestName + " expected response code:" + iExpectedRespCode + " but received:" + iRespCode);
			testRunner.fail("Failed malformed client credential grant access token request: " + strTestName + " expected response code:" + iExpectedRespCode + " but received:" + iRespCode);
			return;						
		}		
	} else {
		log.info("skipping: " + strTestName);
	}	

	iTestID++;
	if(iTestID > 5){
		break;
	}
