con:config.script:
import java.net.*;
import java.io.*;

int issueGET(String targetURL,String strToken){

	log.info("Issuing malformed GET " + targetURL);
	int iRespCode = 0;
	String urlParameters = "";

	URL url;

	HttpURLConnection connection = null;  

	try {
		//Create connection
		url = new URL(targetURL);
		connection = (HttpURLConnection)url.openConnection();
		connection.setRequestMethod("GET");
		connection.setInstanceFollowRedirects(false);
		connection.setRequestProperty("Authorization", "Basic " + strToken);
		connection.setRequestProperty("Accept", "application/json, application/*+json");

		//Get Response    
		InputStream is = connection.getInputStream();
		BufferedReader rd = new BufferedReader(new InputStreamReader(is));
		String line;
		StringBuffer response = new StringBuffer(); 
		while((line = rd.readLine()) != null) {
			response.append(line);
			response.append('\r');
		}
		rd.close();
		//log.info response.toString();

		log.info "Response = " + connection.getResponseCode();

		iRespCode = connection.getResponseCode();

	} catch (Exception e) {
		log.info e.getMessage();
		return 0;
	} finally {
		if(connection != null) {
			log.info "Response = " + connection.getResponseCode();
			iRespCode = connection.getResponseCode();		
			connection.disconnect(); 
		}

		return iRespCode;
	}	
}


propCreateAuthResults = context.testCase.getTestStepByName("Run TestCase Create Authorization");

String strRedirURI = propCreateAuthResults.getPropertyValue("redirect_uri");
String strAuthorizationServerTokenEndpoint = propCreateAuthResults.getPropertyValue("authorizationServerTokenEndpoint");

int iTestID= 0;
while(true) {
	// loop through different malformation tests
	String strTestName = "";

	String strQryParams = "?grant_type=refresh_token&refresh_token=" + propCreateAuthResults.getPropertyValue("refresh_token") + "&scope=" + propCreateAuthResults.getPropertyValue("scope") ;
	String strToken = "dGhpcmRfcGFydHk6c2VjcmV0";

	int iRespCode = 0;
	int iExpectedRespCode = 0;

	switch(iTestID) {
		case 0:
			strTestName = "Malformed Refresh Token Request (No grant_type= field-value pair)	OAD048";
			strQryParams = "?grant_type=refresh_token&refresh_token=" + propCreateAuthResults.getPropertyValue("refresh_token") + "&scope=" + propCreateAuthResults.getPropertyValue("scope") ;			
			//iExpectedRespCode = 401;
			iExpectedRespCode = 200;
			break;
		case 1:
			strTestName = "Malformed Refresh Token Request (No refresh_token= field-value pair)	OAD049";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		case 2:
			strTestName = "Malformed Refresh Token Request (No scope= field-value pair)	OAD050";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		case 3:
			strTestName = "Malformed Refresh Token Request (Multiple grant_type= field-value pairs)	OAD051";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		case 4:
			strTestName = "Malformed Refresh Token Request (Multiple refresh_token= field-value pairs)	OAD052";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		case 5:
			strTestName = "Malformed Refresh Token Request (Multiple scope field-value pairs)	OAD053";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		case 6:
			strTestName = "Invalid Refresh Token Request (Contents of refresh_token= field-value pair was issued to a different Third Party)	OAD054";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		case 7:
			strTestName = "Invalid Refresh Token Request (Contents of scope= field-value pair does not match "scope=" used in original access token request)	AOD055";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		case 8:
			strTestName = "Invalid Refresh Token Request (Value found in refresh_token= field-value pair has expired)	AOD056";
			//strQryParams = "";			
			//iExpectedRespCode = 401;
			break;
		default:
			break;
	}

	// 0 indicates skip test
	if(iExpectedRespCode != 0) {
		log.info("testing: " + strTestName);
		iRespCode = iRespCode = issueGET(strAuthorizationServerTokenEndpoint + strQryParams,strToken);

		if(iRespCode != iExpectedRespCode) {
			log.error("Failed malformed refresh token request: " + strTestName + " expected response code:" + iExpectedRespCode + " but received:" + iRespCode);
			testRunner.fail("Failed malformed refresh token request: " + strTestName + " expected response code:" + iExpectedRespCode + " but received:" + iRespCode);
			return;						
		}		
	} else {
		log.info("skipping: " + strTestName);
	}	

	iTestID++;
	if(iTestID > 8){
		break;
	}
}	



