import groovyx.net.http.HTTPBuilder;
import static groovyx.net.http.Method.GET;
import static groovyx.net.http.ContentType.TEXT;
import java.net.*;
import java.io.*;


String parseEndpointFromURI(String strURI) throws Exception {

    URL aURL = new URL(strURI);
/*
    log.info("protocol = " + aURL.getProtocol()); //http
    log.info("authority = " + aURL.getAuthority()); //example.com:80
    log.info("host = " + aURL.getHost()); //example.com
    log.info("port = " + aURL.getPort()); //80
    log.info("path = " + aURL.getPath()); //  /docs/books/tutorial/index.html
    log.info("query = " + aURL.getQuery()); //name=networking
    log.info("filename = " + aURL.getFile()); ///docs/books/tutorial/index.html?name=networking
    log.info("ref = " + aURL.getRef()); //DOWNLOADING
*/
    return aURL.getProtocol() + "://" +  aURL.getHost() + ":" + aURL.getPort();
}

String parseResource(String strURI) throws Exception {
    URL aURL = new URL(strURI);
    return aURL.getPath();
}

public class ResourceTester {
	Object log;
	String _strResponseBody = null;

	ResourceTester(Object theLog)
	{
		log = theLog;
	}

	public String doGET(String strBaseEndpoint,String href, String token,Map<String, Serializable> params) {
		_strResponseBody = null;

	  	def http = new HTTPBuilder();

		http.request( strBaseEndpoint, GET, TEXT ) { req ->
			uri.path = href
			uri.query = params
		     headers.Authorization = 'Basic ' + token
			headers.Accept = 'application/json, application/*+json'

			response.success = { resp, reader ->
				_strResponseBody = reader.text;				
			}		

			response.failure = { resp ->
				log.error("GET Failed: " + resp.statusLine);
				return null;
			}  
		}
		return _strResponseBody;
	}	
}

